mapper.insert:
INSERT INTO "DEFAULT_COMMENT"
VALUES( SEQ_DEFAULT_COMMENT_NO.NEXTVAL,
#{commentContent},
DEFAULT, DEFAULT,
#{memberNo},


	#{parentCommentNo}




	NULL

,
#{boardNo}
)

mapper.select:
SELECT COUNT(*) FROM "DEFAULT_COMMENT_LIKE"
WHERE COMMENT_NO = #{commentNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM "DEFAULT_COMMENT_LIKE"
WHERE COMMENT_NO = #{commentNo}
AND MEMBER_NO = #{memberNo}
