mapper.update:
UPDATE "BOARD"
SET BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent}
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

UPDATE "BOARD" SET
BOARD_DEL_FL= 'Y'
WHERE BOARD_CODE =
#{boardCode}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}
