mapper.insert:
INSERT INTO "HASHTAG" (TAG_NO, TAG_NAME) SELECT SEQ_TAG_NO.NEXTVAL, tagName FROM (
SELECT #{tag} AS tagName FROM DUAL
) T WHERE NOT EXISTS ( SELECT 1 FROM HASHTAG H WHERE H.TAG_NAME = T.tagName )

INSERT INTO "BOARD_HASHTAG" (BOARD_NO, TAG_NO)

    SELECT #{boardNo}, (
        SELECT TAG_NO FROM HASHTAG WHERE TAG_NAME = #{tag}
    ) FROM DUAL

mapper.update:
UPDATE "RECIPE_BOARD" SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM "RECIPE_BOARD_LIKE"
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}
